import { useState } from 'react';
import { useImmer } from 'use-immer';
// immutable 不可变的编程
function Test3() {
  const [count, setCount] = useState(0);
  const [data, setData] = useImmer({ name: 'dell', age: 18 });
  const [array, setArray] = useImmer([1, 2, 3]);
  // count = 2;// 违背 快照里面的值不可变
  function handleClick() {
    // 修改值了之后重新渲染 但是快照里面count的值不会变 称之为 immutable 不可变的编程
    setCount(count + 1);
  }
  // 快照里的数据 不能直接去更改其值 需要构造一个新的容器 在下次渲染中使用新的容器修改
  function handleObjClick() {
    // 报错
    // data.name = 'lee';
    // setData(data);
    // useState 写法
    // const newData = { ...data };
    // newData.name = 'lee';
    // setData(newData);
    // useImmer 写法
    // immer 会自动帮我们构造一个新的容器 并且把旧的容器的值拷贝过来 
    // 并且把新的容器返回 旧的容器不会被改变 但是新的容器会被改变 
    setData((draft) => {
      draft.name = data.name + 'lee';
    });
  }
  return (
    <>
      <div
        onClick={() => {
          handleClick();
        }}>
        {count}
      </div>
      <div
        onClick={() => {
          handleObjClick();
        }}>
        {data.name}
        <br />
        {data.age}
      </div>
    </>
  );
}
export default Test3;
