import { useState } from 'react';
import Board from './Board';
import History from './History';
function calculateWinner(squares) {
  const lines = [
    [0, 1, 2], // 横
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6], // 竖
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8], // 斜
    [2, 4, 6],
  ];
  // 判断是否三个都存在 上面二位数组为所有可能赢的棋子布局 如果下述三个都存在 并且相等 则代表胜利
  for (let i = 0; i < lines.length; i++) {
    // const [a, b, c] = lines[i];
    const a = lines[i][0];
    const b = lines[i][1];
    const c = lines[i][2];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      // return squares[a];
      return `${squares[a]}`;
    }
  }
  const filledSquares = squares.filter((item) => item === 'X' || item === 'O');
  console.log('filledSquares: ', filledSquares);
  console.log('filledSquares.length: ', filledSquares.length);
  if (filledSquares.length === 9) {
    return 'Nobody Win';
  }
  return null;
}
function Game() {
  const [squares, setSquares] = useState(Array(9).fill(null));

  // 是否已经存在胜利者
  const winner = calculateWinner(squares);
  return (
    <div className='game'>
      <div className='game-board'>
        <Board squares={squares} setSquares={setSquares} winner={winner} />
      </div>
      <div className='game-history'>{winner!='' ? <History /> : null}</div>
    </div>
  );
}

export default Game;
