import { useState } from 'react';
import {useImmer} from 'use-immer';
// immutable 不可变的编程
function Test3() {
  const [count, setCount] = useState(0);
  const [data, setData] = useImmer({ name: 'dell', age: 18 });
  // count = 2;// 违背 快照里面的值不可变
  function handleClick() {
    // 修改值了之后重新渲染 但是快照里面count的值不会变 称之为 immutable 不可变的编程
    setCount(count + 1);
  }
  // 快照里的数据 不能直接去更改其值 需要构造一个新的容器 在下次渲染中使用新的容器修改
  function handleObjClick() {
    data.name = 'lee';
    setData(data);
    // const newData = { ...data };
    // newData.name = 'lee';
    // setData(newData);
  }
  return (
    <>
      <div
        onClick={() => {
          handleClick();
        }}>
        {count}
      </div>
      <div
        onClick={() => {
          handleObjClick();
        }}>
        {data.name}
        <br />
        {data.age}
      </div>
    </>
  );
}
export default Test3;
