import { useState } from 'react';
// immutable 不可变的编程
function Test3() {
  const [count, setCount] = useState(0);
  // count = 2;// 违背 快照里面的值不可变
  function handleClick() {
    // 修改值了之后重新渲染 但是快照里面的值不会变 
    setCount(count + 1);
  }
  return (
    <>
      <div
        onClick={() => {
          handleClick();
        }}>
        {count}
      </div>
    </>
  );
}
export default Test3;
