import { useState } from 'react';
// import Child from './Child';

function Game() {
  console.log('excute Game');
  let name = 'dell';
  const [nameNew, setNameNew] = useState('dellNew');
  const [userInfo, setUserInfo] = useState({
    name: 'dell',
    age: 18,
  });
  const handleUserInfo = () => {
    // userInfo.name = 'lee';
    // setUserInfo(userInfo);
    let userInfoNew = {...userInfo};
    userInfoNew.name = 'leeNew';
    setUserInfo(userInfoNew);
  }
  return (
    <>
      <div
        onClick={() => {
          name = 'lee';
          setNameNew('leeNew');
        }}>
        {name}
        <br />
        {nameNew}
      </div>
      <div style={{ marginTop: '20px' }}>
        <p onClick={handleUserInfo}>{userInfo.name}</p>
        <br />
        {userInfo.age}
      </div>
    </>
  );
}

export default Game;
