// 从命令式开发到声明式开发
// 命令式：你一步步指路 告诉出租车司机怎么回家

import { useImmer } from 'use-immer';

// 声明式: 你只需要告诉出租车司机你要回家就行了
function Test5() {
  const [user, setUser] = useImmer({
    name: 'dell',
    age: 18,
  });
  const userClick = () => {
    setUser((draft) => {
      draft.name = 'lee';
    });
  };
  return (
    <>
      <div
        onClick={() => {
          userClick();
        }}>
        {user.name}
      </div>
      <div>{user.age}</div>
    </>
  );
}

export default Test5;
