import { useState } from 'react';
import Square from './Square';
function getNextLetter(squares) {
  const filledSquares = squares.filter((item) => item === 'X' || item === 'O');
  const filedNumber = filledSquares.length;
  const nextLetter = filedNumber % 2 === 0 ? 'X' : 'O';
  return nextLetter;
}
function calculateWinner(squares) {
  const lines = [
    [0, 1, 2], // 横
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6], // 竖
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8], // 斜
    [2, 4, 6],
  ];
  // 判断是否三个都存在 上面二位数组为所有可能赢的棋子布局 如果下述三个都存在 并且相等 则代表胜利
  for (let i = 0; i < lines.length; i++) {
    // const [a, b, c] = lines[i];
    const a = lines[i][0];
    const b = lines[i][1];
    const c = lines[i][2];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      // return squares[a];
      return `${squares[a]}`;
    }
  }
  const filledSquares = squares.filter((item) => item === 'X' || item === 'O');
  console.log('filledSquares: ', filledSquares);
  console.log('filledSquares.length: ', filledSquares.length);
  if (filledSquares.length === 9) {
    return 'Nobody Win';
  }
  return null;
}
function Board() {
  const [squares, setSquares] = useState(Array(9).fill(null));
  const nextLetter = getNextLetter(squares);

  // let status = null;

  const winner = calculateWinner(squares);
  const status = winner ? `Winner is ${winner}` : `Next player ${nextLetter}`;

  // if (winner) {
  //   status = `Winner ${winner}`;
  // } else if (nextLetter) {
  //   status = `Next player ${nextLetter}`;
  // }

  const clickHandler = (i) => {
    // 1、判断是否有人胜利
    if (winner) {
      return;
    }
    if (!squares[i]) {
      // 1、提取新数组
      const newSquare = squares.slice();
      // 2、修改新数组
      newSquare[i] = nextLetter;
      // 3、更新状态 只有数组的引用发生变化时 才会触发视图更新
      setSquares(newSquare);
    }
  };
  return (
    // 虚拟根节点
    <>
      <div>{status}</div>
      <div className='broad-row'>
        <Square value={squares[0]} index={0} onClick={clickHandler} />
        <Square value={squares[1]} index={1} onClick={clickHandler} />
        <Square value={squares[2]} index={2} onClick={clickHandler} />
      </div>
      <div className='broad-row'>
        <Square value={squares[3]} index={3} onClick={clickHandler} />
        <Square value={squares[4]} index={4} onClick={clickHandler} />
        <Square value={squares[5]} index={5} onClick={clickHandler} />
      </div>
      <div className='broad-row'>
        <Square value={squares[6]} index={6} onClick={clickHandler} />
        <Square value={squares[7]} index={7} onClick={clickHandler} />
        <Square value={squares[8]} index={8} onClick={clickHandler} />
      </div>
    </>
  );
}

export default Board;
