import { useState } from 'react';
// 组件上在页面上被销毁 状态会情况
// 组件所处的UI 节点位置发生变化时 组件会被销毁 
function Test7({ useColor }) {
  const [count, setCount] = useState(0);
  return (
    <div
      style={{ color: useColor ? 'red' : 'black' }}
      onClick={() => {
        setCount(count + 1);
      }}>
      {count}
    </div>
  );
}

export default Test7;
